import { useEffect, useState } from "react";
import Layout from "../Layout";
import axios from "axios";

function BlogLists() {
    const [data,showData] = useState([]);

    useEffect(()=> {
    axios.get("http://localhost:8080/show")
    .then((response)=> {showData(response.data)
        console.log(response.data)
    })
    .catch((error)=> console.log(error))
},[])

    const display = data && data.map((e)=> {
        return <>
        <div key = {e._id}>
        <img src = {`http://localhost:8080/uploads/${e.image}`} width={200}/>
        <h3>{e.title}</h3>
        <p>{e.description}</p>
        <img src = {`http://localhost:8080/uploads/${e.authorImg}`} width = {40}/>
        </div>
        </>
    })
    return (
        <>
        <Layout/>
        <div>{display}</div>
        </>
    )
}

export default BlogLists;
