import { blog_data } from "../Assets/assets";
import { assets } from "../Assets/assets";
function Blogitem() {

    const display = blog_data && blog_data.map((item,index)=> {
        return <>
        <div key={index}>
            <img src = {item.image} height={400} width={400} alt = ''/>
            <p>{item.category}</p>
            <h3>{item.title}</h3>
            <p>{item.description}</p>
            <button>Read More <img src = {assets.arrow} width={12}/></button>
        </div>
        </>
    })
    return (
        <>
        <div id = "blogitem-div">
            <ul>
                <li>All</li>
                <li>Technology</li>
                <li>Startup</li>
                <li>Lifestyle</li>
            </ul>
        </div>
        <div>
            
        </div>
        </>
    )
}

export default Blogitem;
